<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Login extends CI_Controller{/*public function  __construct(){    parent::__construct();}*/ function  index(){     if($this->session->has_userdata('is_logged_in')==1){             redirect('dashboard');     }     $data['title']='تسجيل الدخول الى نظام إدارة شركة الأثير تيك لتكنولوجيا المعلومات';     $data['metakeyword']=' شركة الأثير تيك لتكنولوجيا المعلومات';     $data['metadiscription']=' شركة الأثير تيك لتكنولوجيا المعلومات';    $this->load->view('admin/users/login',$data); }    public  function check_login(){        $this->load->model('Users');        $userdata=$this->Users->check_user_data();        if($userdata !=''){            $userdata['is_logged_in']=true;            $this->session->set_userdata($userdata);            redirect('dashboard');        }else{            $data['title']='تسجيل الدخول الى نظام إدارة شركة الأثير تيك لتكنولوجيا المعلومات';            $data['metakeyword']=' شركة الأثير تيك لتكنولوجيا المعلومات';            $data['metadiscription']=' شركة الأثير تيك لتكنولوجيا المعلومات';            $data['response']="خطأفى بيانات الادخال";            $this->load->view('admin/users/login',$data);        }    }    public function logout(){        $this->session->sess_destroy();        redirect('/login','refresh');    }}