<?phpclass Groups extends  CI_Model{    public function __construct() {        parent::__construct();    }    public function addGroup(){        $this->db->select_max('group_order');        $res1 = $this->db->get('groups');        $res2 = $res1->result_array();        $group_ordermax = $res2[0]['group_order']+1;        $data = array('group_title'=>  $this->input->post('group_title'),            'group_link'=>$this->input->post('group_link'),            'group_order'=>$group_ordermax,            'group_icon_code'=>$this->input->post('group_icon_code'));           $insert= $this->db->insert('groups', $data);        if($insert==1){            return true;        }else{            return false;        }    }    public function record_count() {        return $this->db->count_all("groups");    }    public function fetch_groups($limit, $start) {        $this->db->limit($limit, $start);        $query = $this->db->get("groups");        if ($query->num_rows() > 0) {            foreach ($query->result() as $row) {                $data[] = $row;            }            return $data;        }        return false;    }public  function getgroupbyid($id){    $query= $this->db->get_where('groups',array('group_id'=>$id));    return $query->row_array();}    function updateGroup($id,$data)    {        $this->db->where('group_id', $id);        if ($this->db->update('groups', $data)) {            return true;        } else {            return false;        }    }}