INSERT INTO `about` (`id`, `content`, `type`, `suspend`, `date`, `date_s`, `publisher`) VALUES
(1, '<p dir=\"RTL\">4لاناؤبتيتلياليال</p>\r\n', 1, 1, '1508619600', '1508671215', '1'),
(2, '<p dir=\"rtl\">إعداد كوادر علمية متخصصة ومتميزة فى المجالات المختلفة كالعلوم الإدارية والمعلوماتية وعلوم الحاسب، والتسويق والكثير من المجالات التي يتطلبها سوق العمل كما تهدف الأكاديمية ان تكون هذه الكوادر قادرة على المنافسة فى سوق العمل من خلال التدريب علي ايدي الخبراء في شتي المجالات ، وتوفير بيئة تعليمية متطورة ومتميزة، وتنمية مهارات الافراد والطلاب العلمية والعملية بما يجعلهم قادرين على الابتكار والتعلم الذاتى والعمل الجماعى .. وتهدف الأكاديمية الى الارتقاء بجودة الخدمة التدريبية في بلداننا العربية <u>من خلال التركيز على: </u></p>\r\n\r\n<ul dir=\"rtl\">\r\n <li>تحديد دقيق للاحتياجات التدريبية الفعلية للشركات والافراد</li>\r\n <li>فريق تدريبي يمتلك المعرفة والقدرة والخبرة العملية</li>\r\n <li>محتوي تدريبي يلبي الاحتياجات المعرفية و التطبيقية</li>\r\n <li>وسائل ومعينات تدريبية مناسبة</li>\r\n <li>ابتكاروتبني طرق جديدة في توصيل الخدمة التدريبية</li>\r\n</ul>\r\n', 2, 1, '1495746000', '1495802492', '1'),
(3, '<p dir=\"rtl\">أي مؤسسة ناجحة لابد أن تتمتع بمجموعة من العناصر الأساسية، والمبادئ العليا التي تعمل على تحقيقها، وهي الرسالة والرؤية والقيم التي ستتبناها المؤسسة وأهدافها الإستراتيجية، ولأن أكاديمية اي بي إس مؤسسة تسعى منذ لحظة إنشاءها الى تحقيق طفرة في عالم التدريب العربي، فإننا قد درسنا كل تلك العناصر بعناية لنضع للأكاديمية الرؤية والرسالة التي تحقق هذا الهدف، ولأن الرؤية والرسالة لا يعبروا إلا عن الإطار العام للمرجعية لهذه المؤسسة، فقد عززناهما بتبني مجموعة من القيم والأهداف الاستراتيجية، وذلك من اجل تدعيم وسائلنا لتحقيق أهدافنا.</p>\r\n\r\n<h2 dir=\"rtl\">القيم التي تتبناها الاكاديمية:</h2>\r\n\r\n<ol dir=\"rtl\">\r\n <li>التميز</li>\r\n <li>التركيز على العملاء</li>\r\n <li>الإبداع</li>\r\n <li>التحالف والتعاون</li>\r\n <li>التطوير المستمر</li>\r\n</ol>\r\n\r\n<h2 dir=\"rtl\">اهدافنا الاستراتيجية :</h2>\r\n\r\n<ol dir=\"rtl\">\r\n <li>ان نكون العلامة التجارية الاولى عربياً في مجال التدريب</li>\r\n <li>تطوير مجال التدريب باعتباره احد مجالات الاستثمار الواعدة</li>\r\n <li>ان نقدم منتج تدريبي متفرد</li>\r\n <li>تطوير علاقات ناجحة ومستمرة مع المجتمع والعملاء والمدربين</li>\r\n</ol>\r\n', 3, 1, '1495746000', '1495802074', '1');

